;(function(e){e.fn.marquee=function(t){return this.each(function(){var n=e.extend({},e.fn.marquee.defaults,t),r=e(this),i,s;n=e.extend({},n,r.data());r.wrapInner('<div class="js-marquee"></div>');r.find(".js-marquee").css({"margin-right":n.gap,"float":"left"}).clone().appendTo(r);r.wrapInner('<div style="width:100000px" class="js-marquee-wrapper"></div>');s=r.find(".js-marquee:first").width()+n.gap;i=r.find(".js-marquee-wrapper");var o=function(){i.css("margin-left",n.direction=="left"?0:"-"+s+"px");i.animate({"margin-left":n.direction=="left"?"-"+s+"px":0},n.speed,"linear",o)};setTimeout(o,n.delayBeforeStart)})};e.fn.marquee.defaults={speed:1e4,gap:20,delayBeforeStart:1e3,direction:"left"}})(jQuery);
